void uploadImages(InputElement uploadInput) {
  if(index < n_pho)
  {
    final files = uploadInput.files;
    if(files != null)
    {
      if (files.length == 1) {
        final file = files[0];
        final reader = new FileReader();
        reader.onLoad.listen((e) {
          //save image data to global variable
          photoData.add(reader.result);
          data = reader.result;
          //preview image data
          showIMG('#photo$index', data);
        });
        reader.readAsDataUrl(file);
      }
    }
  }
  if(index == 4)
  {
    final files = uploadInput.files;
    if(files != null)
    {
      if (files.length == 1) {
        final file = files[0];
        final reader = new FileReader();
        reader.onLoad.listen((e) {
          //save image data to global variable
          photoData.add(reader.result);
          data = reader.result;
          //swap image data
          swapImage();
          index--;
        });
        reader.readAsDataUrl(file);
      }
    }
  }
  index++;
}

