processPNG(String data) {
  
  //print('Data: $data');//testing received string
  
  //remove first [ and last ]
  if(data!= null)
  {
    //rearrage news string
    List<int> startCol = new List<int>();
    startCol.add(0);
    List<int> endCol = new List<int>();
    int indexI = 0, indexJ = 0;
    while(indexI !=-1)
    {
    int tmp =data.indexOf('{', indexI+1);
    if(tmp != -1)
    {
    startCol.add(tmp);
    }
    indexI = tmp;
    }
    while(indexJ !=-1)
    {
    int tmp =data.indexOf('}', indexJ+1);
    if(tmp != -1)
    {
    endCol.add(tmp);
    }
    indexJ = tmp;
    }

    //get news list
    List<String> newsList = new List<String>();
    for(int i=0;i<startCol.length;i++)
    {
      String sli = data.slice(startCol[i], endCol[i]+1);
      String tmp = sli.slice(1, sli.length-1);
      newsList.add(tmp);
      //print(tmp);
    }
    
    //foreach news sperate each photo, save each photo of each news in a List<String>
    //save List<String> inside of a big List for all news
    // newsRow: photo1,photo2...
    // newsRow: photo1,photo2,photo3,photo4
    // newsRows: news1, news2....
    List<List<int>> rows = new List<List<int>>();
    for (int j = 0; j< newsList.length; j++)
    {
      List<int> row = new List<int>();
      row.add(0);
      
      String str = newsList[j];
      int index = 0;
      while(index != -1)
      {
        int tmp = str.indexOf('data:image', index+1);
        if(tmp != -1)
        {
        row.add(tmp);
        }
        index = tmp;
      }
      rows.add(row);
    }
    
    //all the news saved here
    List<List<String>> newsRows = new List<List<String>>();
    // transfer index into sliced string
    for(int m =0;m<rows.length;m++)
    {
      String temp = newsList[m];
      List<String> newsRow = new List<String>();
      
      List<int> row = rows[m];
      int rl = row.length;
      
        if(rl == 1)
        { 
          String str = temp;
          newsRow.add(str);
        }
        else if(rl == 2)
        {
          String str =  temp.slice(row[0], row[1]);
          String str2 = temp.slice(row[1]);
          newsRow.add(str);
          newsRow.add(str2);
        }
        else if(rl == 3)
        {
          String str =  temp.slice(row[0], row[1]);
          String str2 = temp.slice(row[1], row[2]);
          String str3 = temp.slice(row[2]);
          newsRow.add(str);
          newsRow.add(str2);
          newsRow.add(str3);
        }
        else if(rl == 4)
        {
          String str =  temp.slice(row[0], row[1]);
          String str2 = temp.slice(row[1], row[2]);
          String str3 = temp.slice(row[2], row[3]);
          String str4 = temp.slice(row[3]);
          newsRow.add(str);
          newsRow.add(str2);
          newsRow.add(str3);
          newsRow.add(str4);
        }
        else
        {
          print('Wrong PNG Data Format!');
        }
      newsRows.add(newsRow);
      //print(newsRow.forEach((value)=>print(value)));
    }
    photoReturned = newsRows;
    //photoReturned = newsMaps; 
  }
  else
  {
    //load default photos when there are no photos.
    print('There are not any photos!');
    photoReturned = null;
  }
  //load Map
  if(jsonData != null && photoReturned != null)
  {
  loadMap(jsonData, photoReturned, markerImg);
  //load album
  createAlbum(photoReturned[0], jsonData[0]);
  }
  else
  {
    print('cant load map due to feed empty!');
  }
  //load table
  if(jsonData != null)
  {
  createTable(jsonData);
  }
  //load first photo
  if(photoReturned != null)
  {
    for(int i=0;i<photoReturned.length;i++)
    {
      ImageElement img = new ImageElement();
      img.src = photoReturned[i][0];
      img.id = ('phototd$i');
      img.height = 30;
      img.width = 30;
      query('#phototable$i').replaceWith(img);
    }
    
    query('#phototd0').onClick.listen((e){
      createAlbum(photoReturned[0], jsonData[0]);
    });
    
    query('#phototd1').onClick.listen((e){ 
      createAlbum(photoReturned[1], jsonData[1]);
    });
    
    query('#phototd2').onClick.listen((e){
      createAlbum(photoReturned[2], jsonData[2]);
    });
    
    query('#phototd3').onClick.listen((e){
      createAlbum(photoReturned[3], jsonData[3]);
    });
  }
}

